const Transport = require('egg-logger').Transport;

class RemoteErrorTransport extends Transport {
  // \u5B9A\u4E49 log \u65B9\u6CD5\u3002\u5728\u6B64\u65B9\u6CD5\u4E2D\uFF0C\u5C06\u65E5\u5FD7\u4E0A\u62A5\u7ED9\u8FDC\u7AEF\u670D\u52A1\u3002
  log(level, args) {
    let log;
    if (args[0] instanceof Error) {
      const err = args[0];
      log = util.format(
        '%s: %s\\n%s\\npid: %s\\n',
        err.name,
        err.message,
        err.stack,
        process.pid
      );
    } else {
      log = util.format(...args);
    }

    this.options.app.curl('http://url/to/remote/error/log/service/logs', {
      data: log,
      method: 'POST'
    })
    .catch(console.error);
  }
}

// \u5728 app.js \u4E2D\u7ED9 errorLogger \u6DFB\u52A0 transport\uFF0C\u8FD9\u6837\u6BCF\u6761\u65E5\u5FD7\u5C31\u4F1A\u540C\u65F6\u6253\u5370\u5230\u8FD9\u4E2A transport\u3002
app.getLogger('errorLogger').set('remote', new RemoteErrorTransport({ level: 'ERROR', app }));
